package Basic;

public class SwitchDemo {

	public static void main(String[] args) {
		
		
		// TODO Auto-generated method stub
		
		int val = 0;
		
		switch (val) {
		case 1:
			System.out.println("Value is 2");

			break; 
			
		case 2:
			System.out.println("Value is 2");
			break;
			
		case 3:	
			System.out.println("Value is 2");

			break;

		default:
			System.out.println("Value is empty");

			break;
		}

	}

}
